## Translation: RNA to Protein

Proteins <- Biostrings::translate (Seq_HoxD) # Translate to protein
print (Proteins) # Print 
  

## Frequency 
Frequency_aa <- alphabetFrequency (Proteins) # Calculate the frequency of aminoacids
print (Frequency_aa) # Print

Frequency_nt <- alphabetFrequency (Seq_HoxD) # Calculate the frequency of nucleotides 
print (Frequency_nt) # Print

## GC% 
Seq_Width <- Biostrings::width (Seq_HoxD) # Calculate the width of the sequences
print (Seq_Width) # Print

GC_P <- (Seq_Width - Frequency_nt [ ,1] - Frequency_nt [ ,4]) * 100 / Seq_Width # Calculate the CG percentage
print (GC_P) # Print

Names <- c ("Amblyraja", "Carcharodon", "Chiloscyllium", "Rhincodon", "Scyliorhinus", "S", "Acipenser", "Callorhinchus", "Polyodon", "Danio", "Mus") # Create a vector with the names 
Data_Frame <- data.frame (Names, GC_P) # Create data frame 

GC_Plot <- ggplot(data = Data_Frame, aes (x = Names, y = GC_P, fill = Names)) + geom_bar (stat = "identity", position = position_dodge ()) + # Create the plot with the specific data
  ggtitle ("GC percentage on the sequences") + # Add the title
  theme (axis.text.x = element_text (angle = 90, vjust = 0.5, size = 10), # Personalize the x labels
         plot.title = element_text (lineheight = 0.8, face = "bold"))  # Personalize the title 
pdf ("03_Output/Plots/GC_Percentage.pdf", width = 10, height = 10) # Save it as PDF with specific measures
print (GC_Plot) # Print
dev.off () # Close the output 
